var longestPalindrome = function (s) {
  if (s === s.toString().split('').reverse().join('')) return s;
  var result = '';
  for (var i = 0; i < s.length; i++) {
    var str = '';
    for (var j = i; j < s.length; j++) {
      str += s[j];
      var strcheck = str;
      if (strcheck[0] != strcheck[strcheck.length - 1]) continue;
      if (str === strcheck.toString().split('').reverse().join('')) {
        if (result.length < strcheck.length) result = strcheck;
      }
    }
  }
  return result;
};

console.log(
  longestPalindrome(
    ''
  )
);
